def print_items(data):
    for i in range(len(data)):
        track = data[i]['track']
        name = track['name']
        artists = track['artists']
        main_artist = artists[0]['name']
        label = ""
        if track['new']:
            label = "NEW"
        print(" {:^4} | {:^80} | {:^40} | {:^5}".format(i + 1, name, main_artist, label))

def create_list(size):
    new_list = []
    for i in range(size):
        new_list.append("empty")
    return  new_list

def getting_priorities(data):
    prio_list = []
    for item in data:
         if item['track']['new'] == True:
             prio_list.append(item)
             data.remove(item)
    return prio_list, data

def set_items(item, new_list, position, playlist_size):
    while new_list[position] != "empty":
            position = (position + 1) % playlist_size
    new_list[position] = item
    return new_list
    
def get_list(data, new_list, PRIORITY, playlist_size):
    prio_list, data = getting_priorities(data)
    for item in prio_list:
        position = random.randint(0, playlist_size - 1) % PRIORITY
        PRIORITY += PRIORITY
        new_list = set_items(item, new_list, position, playlist_size)
    for item in data:
        position = random.randint(0, playlist_size - 1)
        new_list = set_items(item, new_list, position, playlist_size)
    return new_list

def __main__():
    SIZE = len(data)
    new_list = create_list(SIZE)
    shuffled = get_list(data, new_list, 5, SIZE)
    print_items(shuffled)

__main__()

